CREATE TABLE `lagou`.`job` (
  `id` INT NOT NULL AUTO_INCREMENT,
  `jobtype` VARCHAR(20) NOT NULL,
  `jobname` VARCHAR(20) NOT NULL,
  `dname` VARCHAR(20) NOT NULL,
  `Jobquality` VARCHAR(20) NOT NULL,
  `Minsal` INT NOT NULL,
  `maxsal` INT NOT NULL,
  `Jobcity` VARCHAR(20) NOT NULL,
  `Jobexperience` VARCHAR(20) NOT NULL,
  `Jobeducation` VARCHAR(20) NOT NULL,
  `Joblightpoint` VARCHAR(40) NOT NULL,
  `Jobinfo` VARCHAR(200) NOT NULL,
  `Jobaddress` VARCHAR(100) NOT NULL,
  `publishtime` DATE NOT NULL,
  `state` INT NOT NULL,
  `Subtimes` INT NOT NULL,
  PRIMARY KEY (`id`));

  
